export default {
  name: "NavLink",
  data() {
    return {
      anim: true
    };
  },
  watch: {
    "$route.name": {
      handler() {
        if (this.$route.name == this.text) {
          this.anim = false;
          this.$store.commit("UPD_CURPAGE", this.slideNum);
        } else this.anim = true;
      },
      deep: true
    }
  },
  props: {
    text: String,
    path: String,
    slideNum: Number
  },
  methods: {
    updateTransition() {
      if (this.$store.state.curPageNum == 0) this.$store.commit("UPD_TRAN", "fade");else if (this.slideNum < this.$store.state.curPageNum) this.$store.commit("UPD_TRAN", "slideLeft");else if (this.slideNum > this.$store.state.curPageNum) this.$store.commit("UPD_TRAN", "slideRight");
    }
  }
};
