export default {
  name: "NavLink",
  props: {
    text: String,
    path: String,
    slideNum: Number
  },
  methods: {
    updateTransition() {
      if (this.slideNum < this.$store.state.curPageNum) this.$store.commit("UPD_TRAN", "slideLeft");
      if (this.slideNum > this.$store.state.curPageNum) this.$store.commit("UPD_TRAN", "slideRight");
      this.$store.commit("UPD_CURPAGE", this.slideNum);
    }

  }
};
